import { Button, Box, CircularProgress, Autocomplete } from "@mui/material";
import { AutoComplete, TextField } from "material-ui";
import { useState } from "react";
import Recipe, {RecipeData} from "./Components/Recipe";





function App() {

    const [recipe, setRecipe] = useState({} as RecipeData)
    const [toLoad, setLoad] = useState(false)
    const [ingredients, setIngredients] = useState([] as string[])

    const func1 = () => {
        console.log("Hello World!");
        getRecipe()
        setLoad(true)
    }

    function loading(){
        return(
            <Box>
                <CircularProgress></CircularProgress>
            </Box>

        )
    }

    async function getRecipe() {

        const requestBody = JSON.stringify({
            ingredients: [
                "tomato",
                "mozzarella",
                "basil",
                "chocolate pasta",
                "olive oil"
            ]
        })

        await fetch('http://localhost:8000/recipes', {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: requestBody,
            })
            .then((response) => response.json())
            .then((data) => {setRecipe(data)
                            setLoad(false)
                            });



    }






    return (
        <>
        <div>
            <h1>Le Perfum√© Merci</h1>
            <Box> Le Contamine</Box>

            <Autocomplete
                multiple
                filterSelectedOptions
                disableCloseOnSelect
                options={["tomato", "mozarella"]}
                onChange={(event: any, newValue: string[]) =>{
                    setIngredients(newValue);
                }}
                renderInput={(params) => (
                    <TextField {...params} label="Ingredients" />
                )}
            />

            <Button onClick={func1}>Pew</Button>

            {toLoad &&
                loading()
            }

            {recipe.title &&
                <Recipe 
                title={recipe.title}
                description={recipe.description}
                ingredients={recipe.ingredients}
                steps={recipe.steps}
                />

            }

        </div>
        </>
    );
}

export default App;
