import { Button, Box, CircularProgress, Autocomplete, TextField } from "@mui/material";
import { useState } from "react";
import Recipe, {RecipeData} from "./Components/Recipe";
import IngredientOptions from "./Ingredients.json";
import ScentOptions from "./Scents.json";
import Image from './files/background.jpg';





function App() {

    const [recipe, setRecipe] = useState({} as RecipeData)
    const [toLoad, setLoad] = useState(false)
    const [ingredients, setIngredients] = useState([] as string[])

    const func1 = () => {
        console.log("Hello World!");
        getRecipe()
        setLoad(true)
    }

    function loading(){
        return(
            <Box>
                <CircularProgress></CircularProgress>
            </Box>

        )
    }

    async function getRecipe() {

        const requestBody = JSON.stringify({
            ingredients:  ingredients

        })

        await fetch('http://localhost:8000/recipes', {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: requestBody,
            })
            .then((response) => response.json())
            .then((data) => {setRecipe(data)
                            setLoad(false)
                            });



    }






    return (
        <>
            <Box sx={{position:'absolute', top: 0, left: 0, right: 0, bottom: 0 }}>
                <Box sx={{
                backgroundImage: `url(${Image})`,
                backgroundSize: "cover",
                height: "30vh",
                opacity: 0.35,
                }}/> 

            <Box sx={{ position:'absolute', top: 0, left: 0, right: 0, bottom: 0 }}>
                <Box><h1>Le Perfum√© Merci</h1></Box>

                <Autocomplete
                    multiple
                    filterSelectedOptions
                    disableCloseOnSelect
                    options={ScentOptions}
                    onChange={(event: any, newValue: string[]) =>{
                        setIngredients(newValue);
                    }}
                    renderInput={(params) => (
                        <TextField {...params} label="Pick the scents" />
                    )
                }
                />

                <Button onClick={func1}>Show me the magic</Button>
                {toLoad && loading()}
                {recipe.title &&
                    <Recipe 
                    title={recipe.title}
                    description={recipe.description}
                    ingredients={recipe.ingredients}
                    steps={recipe.steps}
                    />

                }
            </Box>
        </Box>
        </>
    );
}

export default App;
